echo 'Records ', $first, ' - ', $last, ' of ', $total;echo '<table border cellpadding="3">';echo '<input type="hidden" name="antixsrf_token" value="';echo $_COOKIE['reg8log_antixsrf_token'];echo '">';require_once $index_dir.'include/func/func_duration2msg.php';echo '<tr style="background: brown; color: #fff"><th></th><th>Uid</th><th>Username</th><th>Gender</th><th>Email</th><th>Time</th><th>Banned</th><th>Delete</th></tr>';$i=0;$r=false;while($rec=$reg8log_db->fetch_row()) {	if(!$r) echo '<tr align="center" style="background: ', $color1,'" onmouseover="highlight(this);" onmouseout="unhighlight(this);"';	else echo '<tr align="center" style="background: ', $color2,'" onmouseover="highlight(this);" onmouseout="unhighlight(this);"';	$i++;	echo ' id="', $i, '">';	$r=!$r;	$row=($page-1)*$per_page+$i;	echo '<td>', $row, '</td>';	echo '<td>', $rec['uid'], '</td>';	echo '<td>', htmlspecialchars($rec['username'], ENT_QUOTES, 'UTF-8'), '</td>';	echo '<td>', $rec['gender'], '</td>';	echo '<td>', $rec['email'], '</td>';	echo '<td>', duration2friendly_str(time()-$rec['timestamp'], 1), ' ago', '</td>';	if($rec['banned']) echo '<td>Yes</td>';	else echo '<td>No</td>';	echo '<td><input type="checkbox" name="', $rec['auto'], '" id="del', $row, '" value="del" onclick="delete_click(', $i, ', ', 'this.checked)"></td>';	echo '</tr>';}echo '<tr align="center"';if(!$r) echo ' style="background: ', $color1;else echo ' style="background: ', $color2;echo '">';echo '<td colspan="7" align="left"><input type="submit" value="Delete selected accounts" style="color: red;"></td><td><input type="button" onclick="check_all(\'del\')" value="All" disabled id="check_all2"></td></tr>';echo '</table>';echo '<script>';echo "\ndocument.getElementById('check_all2').disabled=false;\n";echo '</script>';if($total>$per_page) {	echo '<br>';	$last=ceil($total/$per_page);	if($last<=$max_page_links) {		for($i=1; $i<=$last; $i++) {			echo '<a class="page" href="admin-accounts.php?per_page=', $per_page, '&page=', $i,'" style="';			if($page==$i) echo ' background: #fff; color: #000; border: 1px solid #000';			echo '">', $i, '</a>&nbsp;&nbsp;';		}	}	else {		echo '<a class="page" href="admin-accounts.php?per_page=', $per_page, '&page=', 1,'" style="';		if($page==1) echo ' background: #fff; color: #000; border: 1px solid #000';		echo '">', 1, '</a>';				$middle_page_links=$max_page_links-3;		if($middle_page_links%2) $before=$after=floor($middle_page_links/2);		else {			$before=ceil($middle_page_links/2);			$after=$before-1;		}		if($page+$after>=$last) {			$end=$last-1;			$start=$end-$middle_page_links+1;		}		else if($page-$before<=2) {			$start=2;			$end=$start+$middle_page_links-1;		}		else {			$start=$page-$before;			$end=$start+$middle_page_links-1;		}				if($start>2) echo '<b>...</b>';		else echo '&nbsp;&nbsp;';				for($i=$start; $i<=$end; $i++) {			echo '<a class="page" href="admin-accounts.php?per_page=', $per_page, '&page=', $i,'" style="';			if($page==$i) echo ' background: #fff; color: #000; border: 1px solid #000';			echo '">', $i, '</a>';			if($i<$end) echo '&nbsp;&nbsp';		}				if($end+1!=$last) echo '<b>...</b>';		else echo '&nbsp;&nbsp;'; 				echo '<a class="page" href="admin-accounts.php?per_page=', $per_page, '&page=', $last,'" style="';		if($page==$last) echo ' background: #fff; color: #000; border: 1px solid #000';		echo '">', $last, '</a>&nbsp;&nbsp;';	}	echo '<br>';}//echo '<br><input type="submit" value="Delete selected accounts"><br>';if($total>$per_pages[0]) {	if($total<=$per_page) echo '<br>';	echo '<br>Records per page: <select name="per_page" onchange="document.accounts_form.change_per_page.click()">';	foreach($per_pages as $value) {		if($value!=$per_page) echo "<option>$value</option>";		else echo "<option selected>$value</option>";	}	echo '</select>&nbsp;<input type="submit" value="Show" name="change_per_page" style="display: visible">';	echo  '<script>	document.accounts_form.change_per_page.style.display="none";	</script>';}?></form><a href="index.php">Admin operations</a><br><br><a href="../index.php">Login page</a></center></body></html>