<?phpif(ini_get('register_globals')) exit("<center><h3>Error: Turn that damned register globals off!</h3></center>");if(!isset($parent_page)) exit("<center><h3>Error: Direct access denied!</h3></center>");if(!isset($index_dir)) $index_dir='';$color1='#aaa';$color2='#ccc';if($page*$per_page>$total) $less=($page*$per_page)-$total;else $less=0;$first=($page-1)*$per_page+1;$last=($page*$per_page-$less);$num=$last-$first+1;?><html><head><meta http-equiv="Content-type" content="text/html;charset=UTF-8" /><META HTTP-EQUIV="CACHE-CONTROL" CONTENT="NO-CACHE"><META HTTP-EQUIV="PRAGMA" CONTENT="NO-CACHE"><META HTTP-EQUIV="EXPIRES" CONTENT="0"><title>Pending accounts</title><style>.page {	font-weight: bold;	color: #fff;	padding: 3px;	border: 1px solid #fff;}</style><script>var tmp;function highlight(row) {tmp=row.style.background;row.style.background="#fff";}function unhighlight(row) {row.style.background=tmp;}function green(id) {tmp=document.getElementById(id).style.background="green";}function red(id) {tmp=document.getElementById(id).style.background="red";}function normal(id) {if(id%2) tmp=document.getElementById(id).style.background='<?php echo $color1 ?>';else tmp=document.getElementById(id).style.background='<?php echo $color2 ?>';}function check_all(action) {	<?php	echo "first=$first;\n";	echo "	num=$num;\n";	?>	for(i=first; i<first+num; i++) {		obj=document.getElementById(action+i);		obj.click();	}}</script></head><body bgcolor="#7587b0"><?phpif(!empty($nonexistent_records)) echo '<center style="color: orange; background: #000; padding: 3px; font-weight: bold; margin-bottom: 5px">Info: ', $nonexistent_records, ' record(s) did not exist.</center>';?><center><form action="" method="post" name="admin_confirmation_form"><?phpecho 'Records ', $first, ' - ', $last, ' of ', $total;echo '<table border cellpadding="3">';echo '<input type="hidden" name="antixsrf_token" value="';echo $_COOKIE['reg8log_antixsrf_token'];echo '">';if(isset($err_msgs)) {	echo '<tr align="center"><td colspan="2"  style="border: solid thin yellow; font-style: italic;"><span style="color: #800">Errors:</span><br />';	foreach($err_msgs as $err_msg) {		$err_msg[0]=strtoupper($err_msg[0]);		echo "<span style=\"color: yellow\" >$err_msg</span><br />";	}	echo '</td></tr>';}require_once $index_dir.'include/func/func_duration2msg.php';echo '<tr style="background: brown; color: #fff"><th></th><th>Username</th><th>Gender</th><th>Email</th><th>Emails sent</th><th>Email verified</th><th>Time</th>';if($can_notify_user_about_admin_action) echo '<th title="Whether this user will be notified (via email) about admin approval/rejection of his registration.">Notify</th>';echo '<th>Approve</th><th>Delete</th><th>?</th></tr>';$i=0;$r=false;while($rec=$reg8log_db->fetch_row()) {if(!$r) echo '<tr align="center" style="background: ', $color1,'" onmouseover="highlight(this);" onmouseout="unhighlight(this);"';else echo '<tr align="center" style="background: ', $color2,'" onmouseover="highlight(this);" onmouseout="unhighlight(this);"';$i++;echo ' id="', $i, '">';$r=!$r;$row=($page-1)*$per_page+$i;echo '<td>', $row, '</td>';echo '<td>', htmlspecialchars($rec['username'], ENT_QUOTES, 'UTF-8'), '</td>';echo '<td>', $rec['gender'], '</td>';echo '<td>', $rec['email'], '</td>';if($can_notify_user_about_admin_action and $rec['notify_user']) echo '<input type="hidden" name="email-', $rec['auto'], '" value="', $rec['email'], '">';echo '<td>', $rec['emails_sent'], '</td>';if($rec['email_verified']) echo '<td>Yes</td>';else echo '<td>No</td>';echo '<td>', duration2friendly_str(time()-$rec['timestamp'], 1), ' ago', '</td>';if($can_notify_user_about_admin_action) echo '<td>', ($rec['notify_user'])? 'Yes':'No', '</td>';echo '<td><input type="radio" name="', $rec['auto'], '" id="appr', $row, '" value="appr" onclick="green(', $i,')"></td>';echo '<td><input type="radio" name="', $rec['auto'], '" id="del', $row, '" value="del" onclick="red(', $i,')"></td>';echo '<td><input type="radio" name="', $rec['auto'], '" id="undet', $row, '" value="undet" checked onclick="normal(', $i,')"></td>';echo '</tr>';}echo '<tr align="center"';if(!$r) echo ' style="background: ', $color1;else echo ' style="background: ', $color2;echo '">';if($can_notify_user_about_admin_action) echo '<td colspan="8">';else echo '<td colspan="7">';echo '&nbsp;</td><td><input type="button" onclick="check_all(\'appr\')" value="All" disabled id="check_all1"></td><td><input type="button" onclick="check_all(\'del\')" value="All" disabled id="check_all2"></td><td><input type="button" onclick="check_all(\'undet\')" value="All" disabled id="check_all3"></td></tr>';echo '</table>';echo '<script>';echo "\ndocument.getElementById('check_all1').disabled=false;\ndocument.getElementById('check_all2').disabled=false;\ndocument.getElementById('check_all3').disabled=false;\n";echo '</script>';if($total>$per_page) {	echo '<br>';	for($i=1; $i<=ceil($total/$per_page); $i++) {		echo '<a class="page" href="admin-pending_accounts.php?per_page=', $per_page, '&page=', $i,'" style="';		if($page==$i) echo ' background: #fff; color: #000; border: 1px solid #000';		echo '">', $i, '</a>&nbsp;&nbsp;';	}	echo '<br>';}echo '<br><input type="submit" value="Submit"><br>';if($total>$per_pages[0]) {	if($total<=$per_page) echo '<br>';	echo '<br>Records per page: <select name="per_page" onchange="document.admin_confirmation_form.change_per_page.click()">';	foreach($per_pages as $value) {		if($value!=$per_page) echo "<option>$value</option>";		else echo "<option selected>$value</option>";	}	echo '</select>&nbsp;<input type="submit" value="Show" name="change_per_page" style="display: visible">';	echo  '<script>	document.admin_confirmation_form.change_per_page.style.display="none";	</script>';}?></form><a href="../index.php">Login page</a></center></body></html>